import './App.css';
import * as React from 'react';

const movies = [
  {
    title: 'Movie One',
    type: 'movie',
    episodes: 1,
    currentEp: 0,
    id: 1,
  },
  {
    title: 'Series One',
    type: 'series',
    episodes: 13,
    currentEp: 1,
    id: 2,
  },
  {
    title: 'Anime Movie One',
    type: 'anime-movie',
    episodes: 1,
    currentEp: 0,
    id: 3,
  },
  {
    title: 'Anime Series One',
    type: 'anime-series',
    episodes: 23,
    currentEp: 4,
    id: 4,
  },
];

//function for using localstorage
const useSemiPersistentState = (key, initialState) => {
  const [value, setValue] = React.useState(
    localStorage.getItem(key) || initialState
  );

  React.useEffect(() => {
    localStorage.setItem(key, value);
  }, [value, setValue]);
};



const List = ({ list, onDeleteItem, onEditItem, onAddItem, episodeCountUp }) => (
  <>
  <button className='button button-add' type="button" onClick={() => onAddItem()}>Add</button>

  <ul>
    <li className="item">
      <span className="noHeader">#</span>
      <span className="titleHeader">Title</span>
      <span className="typeHeader">Type</span>
      <span className="episodesHeader">Episodes</span>
    </li>
    {list.map((item) => (
      <li key={item.id} className="item" id={item.id}>
        <span className="number">{item.id}</span>
        <span className="title">{item.title}</span>
        <span className="type">{item.type}</span>
        <span className="episodes">
          <span onClick={() => console.log('subtrack an episode if it\'s more than 1 and not a movie ')} style={{color: 'blue', fontSize: '20px', cursor: 'pointer'}}>-</span>
          {item.currentEp}/{item.episodes}
          <span onClick={episodeCountUp} style={{color: 'blue', fontSize: '20px', cursor: 'pointer'}}>+</span>
        </span>
        <span>
          <button className="button button-edit" type="button" onClick={() => onEditItem(item)}>Edit</button>
        </span>
        <span>
          <button className="button button-delete" type="button" onClick={() => onDeleteItem(item)}>Delete</button>
        </span>
      </li>
    ))}
  </ul>
  </>
);

