import './App.css';
import * as React from 'react';

const movies = [
  {
    title: 'Movie One',
    type: 'movie',
    episodes: 1,
    currentEp: 0,
    id: 1,
  },
  {
    title: 'Series One',
    type: 'series',
    episodes: 13,
    currentEp: 1,
    id: 2,
  },
];

//function for using localstorage
const useSemiPersistentState = (key, initialState) => {
  const [value, setValue] = React.useState(
    localStorage.getItem(key) || initialState
  );

  React.useEffect(() => {
    localStorage.setItem(key, value);
  }, [value, setValue]);
};

const List = ({ list }) => (
  <ul>
    <li className="item">
    <span className="titleHeader">Title</span>
    <span className="typeHeader">Type</span>
    <span className="episodesHeader">Episodes</span>
    </li>
    {list.map((item) => (
      <li key={item.id} className="item">
        <span className="title">{item.title}</span>
        <span className="type">{item.type}</span>
        <span className="episodes">{item.currentEp}/{item.episodes}</span>
      </li>
    ))}
  </ul>
);

const App = () => {

  return (
    <>
      <List list={movies}></List>
    </>
  );
};

export default App;
