import './App.css';
import * as React from 'react';

const movies = [
  {
    title: 'Movie One',
    type: 'movie',
    episodes: 1,
    currentEp: 0,
    id: 1,
  },
  {
    title: 'Series One',
    type: 'series',
    episodes: 13,
    currentEp: 1,
    id: 2,
  },
  {
    title: 'Anime Movie One',
    type: 'anime-movie',
    episodes: 1,
    currentEp: 0,
    id: 3,
  },
  {
    title: 'Anime Series One',
    type: 'anime-series',
    episodes: 23,
    currentEp: 4,
    id: 4,
  },
];

//function for using localstorage
const useSemiPersistentState = (key, initialState) => {
  const [value, setValue] = React.useState(
    localStorage.getItem(key) || initialState
  );

  React.useEffect(() => {
    localStorage.setItem(key, value);
  }, [value, setValue]);
};



const List = ({ list, onDeleteItem, onEditItem, onAddItem, episodeCountUp, episodeCountDown }) => (
  <>
  <button className='button button-add' type="button" onClick={() => onAddItem()}>Add</button>

  <ul>
    <li className="item">
      <span className="noHeader">#</span>
      <span className="titleHeader">Title</span>
      <span className="typeHeader">Type</span>
      <span className="episodesHeader">Episodes</span>
    </li>
    {list.map((item) => (
      <li key={item.id} className="item" id={item.id}>
        <span className="number">{item.id}</span>
        <span className="title">{item.title}</span>
        <span className="type">{item.type}</span>
        <span className="episodes">
          <span onClick={episodeCountDown} style={{color: 'blue', fontSize: '20px', cursor: 'pointer'}}>-</span>
          {item.currentEp}/{item.episodes}
          <span onClick={episodeCountUp} style={{color: 'blue', fontSize: '20px', cursor: 'pointer'}}>+</span>
        </span>
        <span>
          <button className="button button-edit" type="button" onClick={() => onEditItem(item)}>Edit</button>
        </span>
        <span>
          <button className="button button-delete" type="button" onClick={onDeleteItem}>Delete</button>
        </span>
      </li>
    ))}
  </ul>
  </>
);

const App = () => {

  const [movie, setMovie] = React.useState(movies);

  //updates the object
  const movieSetter = (newValue) => {
    setMovie({
      ...movie,
      newValue,
    }, [movie]);
  }; 

  //adds to current episode counter
  const episodeCountUp = event => {

    const item = movies.filter(movie => movie.id === parseInt(event.target.parentElement.parentElement.id));

    if (item[0].currentEp < item[0].episodes) {
      item[0].currentEp++;
    }

    movieSetter(item);
  };

  //subtracks from current episode counter
  const episodeCountDown = event => {
    const item = movies.filter(movie => movie.id === parseInt(event.target.parentElement.parentElement.id));

    if (item[0].currentEp > 0) {
      item[0].currentEp--;
    }

    movieSetter(item);
  }

  const handleAddMovie= () => {

  };

  //remove a movie
  const handleRemoveMovie = event => {

    const item = movies.filter(movie => movie.id !== parseInt(event.target.parentElement.parentElement.id));

    setMovie({
      test: 'test',
    });

    console.log(item);

    console.log(movie);

  };

  const handleEditMovie = (item) => {

  };

  return (
    <>
      <List episodeCountDown={episodeCountDown} episodeCountUp={episodeCountUp} onAddItem={handleAddMovie} onEditItem={handleEditMovie} onDeleteItem={handleRemoveMovie} list={movies}></List>
    </>
  );
};

export default App;
